            // 24. Список всех маршрутов с кондукторами
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], [Места оплаты].Кондуктор FROM (Маршруты INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта]) INNER JOIN [Места оплаты] ON Маршруты.[Места оплаты] = [Места оплаты].[Код места оплаты] WHERE Маршруты.Работает = TRUE AND [Места оплаты].Кондуктор = TRUE",
            // 25. Список всех маршрутов, работающих по воскресеньям
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], Switch([Время работы].[График по месяцам]=1,\"Ежегодно\", [Время работы].[График по месяцам]=2,\"В летний период\", [Время работы].[График по месяцам]=3,\"В течение учебного года\", [Время работы].[График по месяцам]=4,\"В зимний период\") AS [График по месяцам], Switch([Время работы].[График по дням недели]=1,\"Ежедневно\", [Время работы].[График по дням недели]=2,\"С понедельника по субботу\", [Время работы].[График по дням недели]=3,\"По будням\", [Время работы].[График по дням недели]=4,\"По выходным\", [Время работы].[График по дням недели]=5,\"По субботам\", [Время работы].[График по дням недели]=6,\"По воскресеньям\") AS [График по дням недели], Format([Время работы].[Время начала работы],\"hh:nn\") AS [Время начала], Format([Время работы].[Время окончания работы],\"hh:nn\") AS [Время окончания] FROM (([Время работы] INNER JOIN Маршруты ON [Время работы].Маршрут = Маршруты.[Код маршрута]) INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта]) INNER JOIN [График по дням недели] ON [Время работы].[График по дням недели] = [График по дням недели].[Код графика] WHERE Маршруты.Работает = TRUE AND [График по дням недели].Воскресенье = TRUE",
            // 26. Список всех маршрутов, работающих после 22:00
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], Switch([Время работы].[График по месяцам]=1, \"Ежегодно\", [Время работы].[График по месяцам]=2, \"В летний период\", [Время работы].[График по месяцам]=3, \"В течение учебного года\", [Время работы].[График по месяцам]=4, \"В зимний период\") AS [График по месяцам], Switch([Время работы].[График по дням недели]=1, \"Ежедневно\", [Время работы].[График по дням недели]=2, \"С понедельника по субботу\", [Время работы].[График по дням недели]=3, \"По будням\", [Время работы].[График по дням недели]=4, \"По выходным\", [Время работы].[График по дням недели]=5, \"По субботам\", [Время работы].[График по дням недели]=6, \"По воскресеньям\") AS [График по дням недели], Format([Время работы].[Время начала работы], \"hh:nn\") AS [Время начала], Format([Время работы].[Время окончания работы], \"hh:nn\") AS [Время окончания] FROM ([Время работы] INNER JOIN Маршруты ON [Время работы].Маршрут = Маршруты.[Код маршрута]) INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта] WHERE Маршруты.Работает = TRUE AND [Время работы].[Время окончания работы] > #22:00:00# OR [Время работы].[Время окончания работы] < #01:00:00#",
            // 27. Список всех маршрутов, работающих до 06:00
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], Switch([Время работы].[График по месяцам]=1, \"Ежегодно\", [Время работы].[График по месяцам]=2, \"В летний период\", [Время работы].[График по месяцам]=3, \"В течение учебного года\", [Время работы].[График по месяцам]=4, \"В зимний период\") AS [График по месяцам], Switch([Время работы].[График по дням недели]=1, \"Ежедневно\", [Время работы].[График по дням недели]=2, \"С понедельника по субботу\", [Время работы].[График по дням недели]=3, \"По будням\", [Время работы].[График по дням недели]=4, \"По выходным\", [Время работы].[График по дням недели]=5, \"По субботам\", [Время работы].[График по дням недели]=6, \"По воскресеньям\") AS [График по дням недели], Format([Время работы].[Время начала работы], \"hh:nn\") AS [Время начала], Format([Время работы].[Время окончания работы], \"hh:nn\") AS [Время окончания] FROM ([Время работы] INNER JOIN Маршруты ON [Время работы].Маршрут = Маршруты.[Код маршрута]) INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта] WHERE Маршруты.Работает = TRUE AND [Время работы].[Время начала работы] < #06:00:00#",
            // 28. Список неработающих маршрутов
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], [Тип маршрута].[Тип маршрута], Начальная.Остановка AS Начальная, Конечная.Остановка AS Конечная FROM (((Маршруты INNER JOIN Остановки AS Начальная ON Маршруты.Начальная = Начальная.[Код остановки]) INNER JOIN Остановки AS Конечная ON Маршруты.Конечная = Конечная.[Код остановки]) INNER JOIN [Тип маршрута] ON Маршруты.[Тип маршрута] = [Тип маршрута].[Код типа маршрута]) INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта] WHERE Маршруты.Работает = FALSE",
            // 29. Список всех маршрутов, где доступна бесплатная пересадка
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], Тарифы.[Бесплатная пересадка] FROM ((Маршруты INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта]) INNER JOIN Тарифы ON Маршруты.[Тариф] = Тарифы.[Код тарифа]) WHERE Маршруты.Работает = TRUE AND Тарифы.[Бесплатная пересадка] = TRUE",
            // 30. Список всех маршрутов, где доступны проездные
            "SELECT Маршруты.[Номер маршрута], [Тип транспорта].[Тип транспорта], Тарифы.Проездные FROM ((Маршруты INNER JOIN [Тип транспорта] ON Маршруты.[Тип транспорта] = [Тип транспорта].[Код транспорта]) INNER JOIN Тарифы ON Маршруты.[Тариф] = Тарифы.[Код тарифа]) WHERE Маршруты.Работает = TRUE AND Тарифы.Проездные = TRUE"
        };
    }
} 